import { useRef } from "react";

export type PostData = {
  id: number;
  title: string;
  author: string;
  text: string;
  pin:number;
};

const Post = ({postData, deletePost}:{postData:PostData, deletePost:(id:number, pin:number)=>void}): JSX.Element => {
  const pinToEdit = useRef(null);
  return (
    <div className="post">
     <p>Id:{postData.id}</p>
     <p>{postData.title}</p>
     <p>{postData.author}</p>
     <p>{postData.text}</p>
     <input ref={pinToEdit} type="number" />
<button onClick={()=>{

    deletePost(postData.id, pinToEdit.current.value)}}>Delete post</button>
    </div>
  );
};

export default Post;
